// GraphVisualizer.js

import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

function GraphVisualizer({ fullPath, processedMovies, startMovieId, endMovieId }) {
    const svgRef = useRef();

    useEffect(() => {
        const width = 800;
        const height = 600;

        const svg = d3
            .select(svgRef.current)
            .attr('width', width)
            .attr('height', height);

        svg.selectAll('*').remove(); // Clear previous content

        // Create a zoomable group
        const zoomGroup = svg.append('g');

        // Add zoom behavior
        const zoom = d3.zoom().on('zoom', (event) => {
            zoomGroup.attr('transform', event.transform);
        });

        svg.call(zoom);

        // Generate nodes and links
        const nodes = processedMovies.map((movie) => ({
            id: movie.id,
            title: movie.title,
            isInPath: fullPath.some((pathMovie) => pathMovie.id === movie.id),
            isStart: movie.id === startMovieId,
            isEnd: movie.id === endMovieId,
        }));

        const nodesMap = new Map(nodes.map((node) => [node.id, node]));

        // Generate links based on movie connections
        const links = [];

        processedMovies.forEach((movie) => {
            movie.neighbors.forEach((neighborId) => {
                if (nodesMap.has(neighborId)) {
                    links.push({
                        source: movie.id,
                        target: neighborId,
                        isInPath:
                            fullPath.some((pathMovie) => pathMovie.id === movie.id) &&
                            fullPath.some((pathMovie) => pathMovie.id === neighborId),
                    });
                }
            });
        });

        // Simulation setup
        const simulation = d3
            .forceSimulation(nodes)
            .force(
                'link',
                d3
                    .forceLink(links)
                    .id((d) => d.id)
                    .distance(100)
            )
            .force('charge', d3.forceManyBody().strength(-100))
            .force('center', d3.forceCenter(width / 2, height / 2));

        // Draw links
        const link = zoomGroup
            .append('g')
            .attr('stroke', '#999')
            .attr('stroke-opacity', 0.6)
            .selectAll('line')
            .data(links)
            .join('line')
            .attr('stroke-width', (d) => (d.isInPath ? 3 : 1))
            .attr('stroke', (d) => (d.isInPath ? '#ff7f0e' : '#999'));

        // Draw nodes
        const node = zoomGroup
            .append('g')
            .selectAll('circle')
            .data(nodes)
            .join('circle')
            .attr('r', 10)
            .attr('fill', (d) =>
                d.isStart
                    ? '#2ca02c'
                    : d.isEnd
                        ? '#d62728'
                        : d.isInPath
                            ? '#ff7f0e'
                            : '#1f77b4'
            )
            .call(
                d3
                    .drag()
                    .on('start', (event, d) => {
                        if (!event.active) simulation.alphaTarget(0.3).restart();
                        d.fx = d.x;
                        d.fy = d.y;
                    })
                    .on('drag', (event, d) => {
                        d.fx = event.x;
                        d.fy = event.y;
                    })
                    .on('end', (event, d) => {
                        if (!event.active) simulation.alphaTarget(0);
                        d.fx = null;
                        d.fy = null;
                    })
            );

        // Add titles to nodes
        const text = zoomGroup
            .append('g')
            .selectAll('text')
            .data(nodes)
            .join('text')
            .attr('dx', 12)
            .attr('dy', 4)
            .text((d) => d.title)
            .attr('font-size', '10px');

        // Tooltip
        const tooltip = d3
            .select('body')
            .append('div')
            .attr('class', 'tooltip')
            .style('opacity', 0);

        node
            .on('mouseover', (event, d) => {
                tooltip
                    .transition()
                    .duration(200)
                    .style('opacity', 0.9);
                tooltip
                    .html(`<strong>${d.title}</strong>`)
                    .style('left', event.pageX + 5 + 'px')
                    .style('top', event.pageY - 28 + 'px');
            })
            .on('mouseout', () => {
                tooltip
                    .transition()
                    .duration(500)
                    .style('opacity', 0);
            });

        // Update positions on each tick
        simulation.on('tick', () => {
            link
                .attr('x1', (d) => d.source.x)
                .attr('y1', (d) => d.source.y)
                .attr('x2', (d) => d.target.x)
                .attr('y2', (d) => d.target.y);

            node.attr('cx', (d) => d.x).attr('cy', (d) => d.y);

            text.attr('x', (d) => d.x).attr('y', (d) => d.y);
        });

        // Cleanup on unmount
        return () => {
            simulation.stop();
            tooltip.remove();
        };
    }, [processedMovies, fullPath, startMovieId, endMovieId]);

    return (
        <svg
            ref={svgRef}
            style={{ width: '100%', height: '600px', border: '1px solid black' }}
        ></svg>
    );
}

export default GraphVisualizer;
