import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

function GraphVisualizer({ movies }) {
    const svgRef = useRef();

    useEffect(() => {
        const svg = d3.select(svgRef.current);
        svg.selectAll('*').remove(); // Clear previous content

        const width = 800;
        const height = 600;

        const nodes = movies.map((movie) => ({
            id: movie.id || movie.movie_id,
            title: movie.title || movie.name,
        }));
        console.log('Nodes:', nodes);

        const links = movies.slice(0, movies.length - 1).map((movie, index) => ({
            source: movie.id,
            target: movies[index + 1]?.id,
        }));
        console.log('Links:', links);

        const simulation = d3.forceSimulation(nodes)
            .force('link', d3.forceLink(links).id((d) => d.id).distance(150))
            .force('charge', d3.forceManyBody().strength(-300))
            .force('center', d3.forceCenter(width / 2, height / 2))

        // Draw links
        const link = svg.append('g')
            .attr('stroke', '#999')
            .attr('stroke-width', 2)
            .selectAll('line')
            .data(links)
            .join('line');

        // Draw nodes
        const node = svg.append('g')
            .selectAll('circle')
            .data(nodes)
            .join('circle')
            .attr('r', 15)
            .attr('fill', '#69b3a2')
            .call(
                d3
                    .drag()
                    .on('start', (event, d) => {
                        if (!event.active) simulation.alphaTarget(0.3).restart();
                        d.fx = d.x;
                        d.fy = d.y;
                    })
                    .on('drag', (event, d) => {
                        d.fx = event.x;
                        d.fy = event.y;
                    })
                    .on('end', (event, d) => {
                        if (!event.active) simulation.alphaTarget(0);
                        d.fx = null;
                        d.fy = null;
                    })
            );

        // Add titles to nodes
        const text = svg.append('g')
            .selectAll('text')
            .data(nodes)
            .join('text')
            .attr('dx', 20)
            .attr('dy', 4)
            .text((d) => d.title)
            .attr('font-size', '12px')
            .attr('fill', '#fff');

        // Update positions on each tick
        simulation.on('tick', () => {
            console.log("Simulation Running");
            link
                .attr('x1', (d) => d.source.x)
                .attr('y1', (d) => d.source.y)
                .attr('x2', (d) => d.target.x)
                .attr('y2', (d) => d.target.y);

            node.attr('cx', (d) => d.x).attr('cy', (d) => d.y);

            text.attr('x', (d) => d.x).attr('y', (d) => d.y);
        });
    }, [movies]);

    return <svg ref={svgRef} width={800} height={600} style={{ border: '1px solid black' }}></svg>;
}

export default GraphVisualizer;
